'use client';

import type { HTMLAttributes } from 'react';
import { forwardRef, useCallback, useImperativeHandle, useRef } from 'react';
import { motion, useAnimation } from 'motion/react';

import { cn } from '@/lib/utils';

export interface AirplayIconHandle {
  startAnimation: () => void;
  stopAnimation: () => void;
}

interface AirplayIconProps extends HTMLAttributes<HTMLDivElement> {
  size?: number;
}

const AirplayIcon = forwardRef<AirplayIconHandle, AirplayIconProps>(
  ({ onMouseEnter, onMouseLeave, className, size = 28, ...props }, ref) => {
    const controls = useAnimation();
    const isControlledRef = useRef(false);

    useImperativeHandle(ref, () => {
      isControlledRef.current = true;
      return {
        startAnimation: () => controls.start('animate'),
        stopAnimation: () => controls.start('normal'),
      };
    });

    const handleMouseEnter = useCallback(
      (e: React.MouseEvent<HTMLDivElement>) => {
        if (!isControlledRef.current) {
          controls.start('animate');
        } else {
          onMouseEnter?.(e);
        }
      },
      [controls, onMouseEnter]
    );

    const handleMouseLeave = useCallback(
      (e: React.MouseEvent<HTMLDivElement>) => {
        if (!isControlledRef.current) {
          controls.start('normal');
        } else {
          onMouseLeave?.(e);
        }
      },
      [controls, onMouseLeave]
    );

    return (
      <div
        className={cn(className)}
        onMouseEnter={handleMouseEnter}
        onMouseLeave={handleMouseLeave}
        {...props}
      >
        <svg
          xmlns="http://www.w3.org/2000/svg"
          width={size}
          height={size}
          viewBox="0 0 24 24"
          fill="none"
          stroke="currentColor"
          strokeWidth="2"
          strokeLinecap="round"
          strokeLinejoin="round"
          className="overflow-visible"
        >
          {/* Base AirPlay triangle */}
          <motion.path
            animate={controls}
            variants={{
              normal: { scale: 1, y: 0 },
              animate: { scale: 1.1, y: -1 },
            }}
            transition={{ duration: 0.3 }}
            d="M6 20h12l-6-6z"
          />

          {/* Signal waves */}
          <motion.path
            animate={controls}
            variants={{
              normal: { pathLength: 0.8, opacity: 0.6 },
              animate: {
                pathLength: [0.8, 1, 0.8],
                opacity: [0.6, 1, 0.6],
                transition: { repeat: Infinity, duration: 1.5 },
              },
            }}
            transition={{ duration: 0.5 }}
            d="M2 10a10 10 0 0 1 20 0"
          />

          <motion.path
            animate={controls}
            variants={{
              normal: { pathLength: 0.8, opacity: 0.4 },
              animate: {
                pathLength: [0.8, 1, 0.8],
                opacity: [0.4, 0.9, 0.4],
                transition: { repeat: Infinity, duration: 1.8, delay: 0.3 },
              },
            }}
            transition={{ duration: 0.5 }}
            d="M4.5 12a7.5 7.5 0 0 1 15 0"
          />

          <motion.path
            animate={controls}
            variants={{
              normal: { pathLength: 0.8, opacity: 0.3 },
              animate: {
                pathLength: [0.8, 1, 0.8],
                opacity: [0.3, 0.8, 0.3],
                transition: { repeat: Infinity, duration: 2.1, delay: 0.5 },
              },
            }}
            transition={{ duration: 0.5 }}
            d="M7 14a5 5 0 0 1 10 0"
          />
        </svg>
      </div>
    );
  }
);

AirplayIcon.displayName = 'AirplayIcon';

export { AirplayIcon };
