'use client';

import type { Transition } from 'motion/react';
import type { HTMLAttributes } from 'react';
import { forwardRef, useCallback, useImperativeHandle, useRef } from 'react';
import { motion, useAnimation } from 'motion/react';

import { cn } from '@/lib/utils';

export interface MicIconHandle {
  startAnimation: () => void;
  stopAnimation: () => void;
}

interface MicIconProps extends HTMLAttributes<HTMLDivElement> {
  size?: number;
}

const DEFAULT_TRANSITION: Transition = {
  type: 'spring',
  stiffness: 200,
  damping: 15,
  mass: 0.5,
};

const MicIcon = forwardRef<MicIconHandle, MicIconProps>(
  ({ onMouseEnter, onMouseLeave, className, size = 28, ...props }, ref) => {
    const controls = useAnimation();
    const isControlledRef = useRef(false);

    useImperativeHandle(ref, () => {
      isControlledRef.current = true;

      return {
        startAnimation: () => controls.start('animate'),
        stopAnimation: () => controls.start('normal'),
      };
    });

    const handleMouseEnter = useCallback(
      (e: React.MouseEvent<HTMLDivElement>) => {
        if (!isControlledRef.current) {
          controls.start('animate');
        } else {
          onMouseEnter?.(e);
        }
      },
      [controls, onMouseEnter]
    );

    const handleMouseLeave = useCallback(
      (e: React.MouseEvent<HTMLDivElement>) => {
        if (!isControlledRef.current) {
          controls.start('normal');
        } else {
          onMouseLeave?.(e);
        }
      },
      [controls, onMouseLeave]
    );

    return (
      <div
        className={cn(className)}
        onMouseEnter={handleMouseEnter}
        onMouseLeave={handleMouseLeave}
        {...props}
      >
        <svg
          xmlns="http://www.w3.org/2000/svg"
          width={size}
          height={size}
          viewBox="0 0 24 24"
          fill="none"
          stroke="currentColor"
          strokeWidth="2"
          strokeLinecap="round"
          strokeLinejoin="round"
        >
          {/* Stand */}
          <path d="M12 19v3" />
          {/* Curved base/holder */}
          <motion.path
            d="M19 10v2a7 7 0 0 1-14 0v-2"
            variants={{
              normal: { scaleY: 1, originY: 0.5 },
              animate: {
                scaleY: [1, 1.02, 1],
                transition: {
                  ...DEFAULT_TRANSITION,
                  delay: 0.1,
                },
              },
            }}
            animate={controls}
          />
          {/* Capsule - main animated element */}
          <motion.rect
            x="9"
            y="2"
            width="6"
            height="13"
            rx="3"
            variants={{
              normal: { scale: 1, originX: 0.5, originY: 0.5 },
              animate: {
                scale: [1, 1.08, 1, 1.05, 1],
                transition: {
                  duration: 0.6,
                  repeat: 2,
                  ease: 'easeInOut',
                },
              },
            }}
            style={{ transformOrigin: '12px 8.5px' }}
            animate={controls}
          />
        </svg>
      </div>
    );
  }
);

MicIcon.displayName = 'MicIcon';

export { MicIcon };
