"use client";

import type { Variants } from "motion/react";
import { motion, useAnimation } from "motion/react";
import type { HTMLAttributes } from "react";
import {
  forwardRef,
  useCallback,
  useImperativeHandle,
  useRef,
  useState,
} from "react";

import { cn } from "@/lib/utils";

export interface RepeatIconHandle {
  startAnimation: () => void;
  stopAnimation: () => void;
}

interface RepeatIconProps extends HTMLAttributes<HTMLDivElement> {
  size?: number;
}

const RIGHT_ARROW_VARIANTS: Variants = {
  idle: {
    opacity: 1,
    x: 0,
  },
  show: {
    opacity: [0, 1],
    x: [-4, 0],
    transition: {
      duration: 0.3,
      ease: "easeOut",
      delay: 0.05,
    },
  },
};

const LEFT_ARROW_VARIANTS: Variants = {
  idle: {
    opacity: 1,
    x: 0,
  },
  show: {
    opacity: [0, 1],
    x: [4, 0],
    transition: {
      duration: 0.3,
      ease: "easeOut",
      delay: 0.05,
    },
  },
};

const RepeatIcon = forwardRef<RepeatIconHandle, RepeatIconProps>(
  ({ onMouseEnter, onMouseLeave, className, size = 28, ...props }, ref) => {
    const [hovered, setHovered] = useState(false);
    const controls = useAnimation();
    const isControlledRef = useRef(false);

    useImperativeHandle(ref, () => {
      isControlledRef.current = true;
      return {
        startAnimation: () => controls.start("animate"),
        stopAnimation: () => controls.start("normal"),
      };
    });

    const handleMouseEnter = useCallback(
      (e: React.MouseEvent<HTMLDivElement>) => {
        if (isControlledRef.current) {
          onMouseEnter?.(e);
        } else {
          controls.start("animate");
        }
      },
      [controls, onMouseEnter]
    );

    const handleMouseLeave = useCallback(
      (e: React.MouseEvent<HTMLDivElement>) => {
        if (isControlledRef.current) {
          onMouseLeave?.(e);
        } else {
          controls.start("normal");
        }
      },
      [controls, onMouseLeave]
    );

    return (
      <div
        className={cn(className)}
        onMouseEnter={(e) => {
          setHovered(true);
          handleMouseEnter(e);
        }}
        onMouseLeave={(e) => {
          setHovered(false);
          handleMouseLeave(e);
        }}
        {...props}
      >
        <motion.svg
          animate={hovered ? "show" : "idle"}
          fill="none"
          height={size}
          initial="idle"
          stroke="currentColor"
          strokeLinecap="round"
          strokeLinejoin="round"
          strokeWidth="2"
          viewBox="0 0 24 24"
          width={size}
          xmlns="http://www.w3.org/2000/svg"
        >
          <motion.g variants={RIGHT_ARROW_VARIANTS}>
            <motion.path d="M3 11v-1a4 4 0 0 1 4-4h14" />
            <motion.path d="m17 2 4 4-4 4" />
          </motion.g>
          <motion.g variants={LEFT_ARROW_VARIANTS}>
            <motion.path d="M21 13v1a4 4 0 0 1-4 4H3" />
            <motion.path d="m7 22-4-4 4-4" />
          </motion.g>
        </motion.svg>
      </div>
    );
  }
);

RepeatIcon.displayName = "RepeatIcon";

export { RepeatIcon };
